import * as unifi from "@pulumiverse/unifi";

const config = new pulumi.Config();
const laptopIps = config.requireObject<Array<string>>("laptopIps");
const canPrint = new unifi.firewall.Group("can_print", {
    name: "can-print",
    type: "address-group",
    members: laptopIps,
});
```
