    purpose: "corporate",
    subnet: "10.0.0.1/24",
    vlanId: vlanId,
    dhcpStart: "10.0.0.6",
    dhcpStop: "10.0.0.254",
    dhcpEnabled: true,
});
const poeDisabled = new unifi.port.Profile("poe_disabled", {
    name: "POE Disabled",
    nativeNetworkconfId: vlan.id,
    poeMode: "off",
});
